package protocol.body

import java.nio.ByteBuffer

import com.github.romangrebennikov.columnize.protocol.body.ResultBody.Rows
import com.github.romangrebennikov.columnize.protocol.body.{ResultBody, QueryBody, EmptyBody}
import com.github.romangrebennikov.columnize.protocol.{Consistency, Opcode, Frame}
import org.apache.commons.codec.binary.Hex
import org.scalatest.{MustMatchers, WordSpecLike}

/**
 * Created by shutty on 10/13/15.
 */
class QueryBodySpec extends WordSpecLike with MustMatchers {
  "QueryBody" must {
    "deserialize system.peers query" in {
      val body = ByteBuffer.wrap(Hex.decodeHex("04000003070000005c0000005553454c45435420706565722c20646174615f63656e7465722c207261636b2c20746f6b656e732c207270635f616464726573732c20736368656d615f76657273696f6e2046524f4d2073797374656d2e7065657273000100".toCharArray))
      val frame = Frame(body)
      assert(frame.opcode == Opcode.QUERY)
      assert(frame.body == QueryBody("SELECT peer, data_center, rack, tokens, rpc_address, schema_version FROM system.peers", Consistency.ANY))
    }
    "deserialize system.peers response" in {
      val body = ByteBuffer.wrap(Hex.decodeHex("84000003080000006b000000020000000100000006000673797374656d000570656572730004706565720010000b646174615f63656e746572000d00047261636b000d0006746f6b656e730022000d000b7270635f616464726573730010000e736368656d615f76657273696f6e000c00000000".toCharArray))
      val frame = Frame(body)
      assert(frame.opcode == Opcode.RESULT)
      assert(frame.body.isInstanceOf[ResultBody])
      val fbody = frame.body.asInstanceOf[ResultBody]
      assert(fbody.content.isInstanceOf[Rows])
      val rows = fbody.content.asInstanceOf[Rows]
      assert(rows.rowsCount == 0)
      assert(rows.meta.columnsCount == 6)
    }
    "deserialize system.local query" in {
      val body = ByteBuffer.wrap(Hex.decodeHex("0400000407000000760000006f53454c45435420636c75737465725f6e616d652c20646174615f63656e7465722c207261636b2c20746f6b656e732c20706172746974696f6e65722c20736368656d615f76657273696f6e2046524f4d2073797374656d2e6c6f63616c205748455245206b65793d276c6f63616c27000100".toCharArray))
      val frame = Frame(body)
      assert(frame.body == QueryBody("SELECT cluster_name, data_center, rack, tokens, partitioner, schema_version FROM system.local WHERE key='local'", Consistency.ANY))
    }
    "deserialize system.local response" in {
      val body = ByteBuffer.wrap(Hex.decodeHex("".toCharArray))
      val frame = Frame(body)
      assert(frame.opcode == Opcode.RESULT)
    }
    "deserialize system.schema_columnfamilies request" in {
      val body = ByteBuffer.wrap(Hex.decodeHex("".toCharArray))
      val frame = Frame(body)
      assert(frame.body == QueryBody("SELECT * FROM system.schema_columnfamilies", Consistency.ANY))
    }
    "deserialize system.schema_columnfamilies response" in {
      val body = ByteBuffer.wrap(Hex.decodeHex("".toCharArray))
      val frame = Frame(body)
      assert(frame.opcode == Opcode.RESULT)
    }
  }
}
